mapper.select:
SELECT tgroup, tamount, mnname, sname, sname, total, sno, tdate, SUM(total) OVER (PARTITION BY tgroup) AS realtotal
FROM tradeview 
WHERE mid=#{id}

SELECT tgroup, tamount, mnname, sname, sname, total, tdate, SUM(total) OVER (PARTITION BY tgroup) AS realtotal
FROM tradeview 
WHERE tgroup=#{tgroup}
